"""
    Generic UDF to generate surrogate key for a table
"""
def sk_generator(keycol,colname,df):
    return spark.sql('select row_number() over (order by "{}") as {}, * from {}'.format(keycol,colname,df))


"""
    Generic UDF to get a substring of a given column
"""
def getsubstring(col,start,end):
    try:
        return str(col)[start:end]
    except:
        pass

"""
    Generic UDF to calculate Age
"""
def calc_age(col):
    cur_year = int(datetime.datetime.now().date().strftime("%Y"))
    try:
        return (cur_year - int(col[0:4]))
    except:
        pass


"""
    Generic UDF to bucketize given column values into a given no of buckets
"""
def bucketize(col,bucket_margins,bucket_names):
    try:
        for i in range(len(bucket_margins)):
            if col < bucket_margins[i]:
                return bucket_names[i]
    except:
        pass

"""
    Generic UDF to bucketize non-numeric columns
"""
def social_state(col,bucket_values,bucket_names):
    try:
        for i in range(len(bucket_values)):
            if col == bucket_values[i]:
                return bucket_names[i]
        else:
            return bucket_names[-1]
    except:
        pass

"""
    Generic UDF to set seller size
"""
def check_seller_size(col,limits):
    try:
        if int(col)> limits[0]:
            return "Small-Scale"
        if int(col)> limits[1]:
            return "Medium-Scale"
        if int(col)> limits[2]:
            return "Mass-Scale"
    except:
        pass


"""
    Generic UDF to get unque list
"""
def get_unique_cat(col):
    try:
        return str(list(set(col)))
    except:
        pass

"""
    Generic UDF to get review sentiment
"""
def get_review_sentiment(col):
    try:
        if int(col)>3:
            return "Very Satisfied"
        elif int(col)==3:
            return "Moderate"
        elif int(col)<3:
            return "Not Satisfied"
    except:
        pass


"""
    Generic UDF to get product volume
"""
def get_product_volume(col_arr):
    try:
        return float(col_arr[0]) * float(col_arr[1]) * float(col_arr[2])
    except:
        pass


"""
    Generic UDF to get package type
"""
def get_package_type(col_arr):
    try:
        if float(col_arr[0]) < 3000 or float(col_arr[1])< 500:
            return "Small"
        if float(col_arr[0]) < 6000 or float(col_arr[1])< 1000:
            return "Medium"
        if float(col_arr[0]) > 5999 or float(col_arr[1])> 999:
            return "Large"
    except:
        pass


"""
    Generic UDF to check discount
"""
def check_discount(col):
    try:
        if int(col)> 0:
            return "YES"
        else:
            return "NO"
    except:
        pass



"""
    Generic UDF to check delayed deliveries
"""
def chekc_delayed_delivery(col_arr):
    try:
        est_month = int((col_arr[0])[5:7])
        est_date  = int((col_arr[0])[8:10])
        act_month  = int((col_arr[1])[5:7])
        act_date  = int((col_arr[1])[8:10])
        if act_month > est_month:
            return "YES"
        elif act_date> est_date:
            return "YES"
        else:
            return "NO"
    except:
        pass
