"""
    Generic UDF to get a substring of a given column
"""
def getsubstring(col,start,end):
    try:
        return str(col)[start:end]
    except:
        pass

"""
    Generic UDF to calculate Age
"""
def calc_age(col):
    cur_year = int(datetime.date.today().strftime("%Y"))
    try:
        return (cur_year - int(col[0:4]))
    except:
        pass


"""
    Generic UDF to bucketize given column values into a given no of buckets
"""
def bucketize(col,bucket_margins,bucket_names):
    try:
        for i in range(len(bucket_margins)):
            if col < bucket_margins[i]:
                return bucket_names[i]
    except:
        pass

"""
    Generic UDF to bucketize non-numeric columns
"""
def social_state(col,bucket_values,bucket_names):
    try:
        for i in range(len(bucket_values)):
            if col == bucket_values[i]:
                return bucket_names[i]
        else:
            return bucket_names[-1]
    except:
        pass
