"""
    Generic UDF to generate surrogate key for a table
"""
def sk_generator(keycol,colname,df):
    return spark.sql('select row_number() over (order by "{}") as {}, * from {}'.format(keycol,colname,df))


"""
    Generic UDF to bucketize given column values into a given no of buckets
"""
def bucketize(col,bucket_margins,bucket_names):
    try:
        for i in range(len(bucket_margins)):
            if col < bucket_margins[i]:
                return bucket_names[i]
    except:
        pass
        

"""
    Generic UDF to calculate Age
"""
def calc_birthyear(col):
    cur_year = int(datetime.datetime.now().date().strftime("%Y"))
    try:
        return (cur_year - int(col))
    except:
        pass


#return a list of all (possibly nested) fields to select, within a given schema 
def flatten(schema, prefix: str = ""): 
# return a list of sub-items to select, within a given field 
    def field_items(field): 
        name = f'{prefix}.{field.name}' if prefix else field.name 
        if type(field.dataType) == StructType: 
            return flatten(field.dataType, name) 
        else: 
            return [col(name)] 
    return [item for field in schema.fields for item in field_items(field)] 

 
# return stringified form of an array 
def array_to_string(arr): 
    return '[' + ','.join([str(elem) for elem in arr]) + ']'

